import React, {useEffect, useRef, useState} from 'react';
import './App.css';
import {ClientEnvelope, PlaceOrder} from "./Models/ClientMessages";
import {ClientMessageType, Instrument, OrderSide} from "./Enums";
import Decimal from "decimal.js";

function App() {
    const [isPaused, setIsPaused] = useState(false);

    const ws = useRef<WebSocket>()
    const [status, setStatus] = useState("");
    const [data, setData] = useState<JSON>()

    useEffect(() => {
        if (!isPaused) {
            ws.current = new WebSocket("ws://localhost:8080/ws/");
            ws.current.onopen = () => setStatus("Open")
            ws.current.onclose = () => setStatus("Close")
            ws.current.onmessage = () => {setData(ws.current)}
        }
        return () => ws.current?.close()
    }, [ws, isPaused])

    const getData = () => {
        ws.current?.send(JSON.stringify({
            messageType: ClientMessageType.placeOrder,
            message: {
                instrument: Instrument.eur_rub,
                side: OrderSide.buy,
                amount: new Decimal(123),
                price: new Decimal(456)
            } as PlaceOrder
        } as ClientEnvelope))
    }

    return (
        <div className="App">
            <header className="App-header">
                <button onClick={() => setIsPaused(!isPaused)}>Change connection</button>
                <button onClick={() => getData()}>123132</button>
                <p>
                    {status}
                </p>
            </header>
        </div>
  );
}

export default App;
